def get_logo_sources(url, name):
    """
    ç”Ÿæˆå¯èƒ½çš„ Logo ä¸‹è½½åœ°å€ã€‚
    ä¼˜å…ˆä½¿ç”¨ Clearbit APIï¼Œå…¶æ¬¡æ˜¯ Google Favicon APIã€‚
    """
    try:
        domain = urlparse(url).netloc.replace("www.", "")
        # ä¸¤ä¸ªå¼ºå¤§çš„å…è´¹ Logo æº
        s1 = f"https://logo.clearbit.com/{domain}?size=128"
        s2 = f"https://www.google.com/s2/favicons?domain={domain}&sz=128"
        return [s1, s2]
    except:
        return []

def setup_directories():
    if not os.path.exists(ASSETS_DIR):
        os.makedirs(ASSETS_DIR)
        print(f"[INFO] Created directory: {ASSETS_DIR}")
    
    js_dir = os.path.dirname(JS_FILE_PATH)
    if not os.path.exists(js_dir):
        os.makedirs(js_dir)
        print(f"[INFO] Created directory: {js_dir}")

def download_image(sources, filename):
    """å°è¯•ä»å¤šä¸ªæºä¸‹è½½å›¾ç‰‡ï¼ŒæˆåŠŸä¸€ä¸ªå³åœæ­¢"""
    local_path = f"{ASSETS_DIR}/{filename}.png"
    
    # å¦‚æœå·²ç»å­˜åœ¨ï¼Œè·³è¿‡ï¼ˆçœæµé‡ï¼‰
    if os.path.exists(local_path):
        return local_path

    for url in sources:
        try:
            # ä¼ªè£… User-Agent
            req = urllib.request.Request(
                url, 
                headers={'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36'}
            )
            with urllib.request.urlopen(req, timeout=5) as response:
                if response.getcode() == 200:
                    data = response.read()
                    if len(data) > 100: # ç®€å•æ£€æŸ¥æ˜¯å¦æ˜¯æœ‰æ•ˆå›¾ç‰‡
                        with open(local_path, 'wb') as f:
                            f.write(data)
                        print(f"[OK] Downloaded: {filename}")
                        return local_path
        except:
            continue
    
    print(f"[WARN] Failed to download: {filename} (will use fallback)")
    return None

def main():
    setup_directories()
    final_data = []
    print(f"[INFO] Processing {len(tools_db)} AI tools... (please wait)")
    
    for i, tool in enumerate(tools_db):
        # 1. ç”Ÿæˆæ–‡ä»¶å
        safe_name = "".join([c for c in tool['name'] if c.isalnum() or c in ('-','_')]).lower()
        
        # 2. è·å–ä¸‹è½½æºå¹¶ä¸‹è½½
        sources = get_logo_sources(tool['url'], tool['name'])
        local_path = download_image(sources, safe_name)
        
        # 3. è¡¥å…… Mock æ•°æ® (ä¿æŒå‰ç«¯å…¼å®¹æ€§)
        # ä» Tags ä¸­çŒœæµ‹å®šä»·
        pricing = "å…è´¹/ä»˜è´¹"
        for t in tool['tags']:
            if t in ["å…è´¹", "ä»˜è´¹", "å¼€æº", "å…è´¹è¯•ç”¨", "å¾…å®š"]:
                pricing = t
